INSERT INTO `wp_posts` VALUES (109,1,'2024-10-10 12:32:18','2024-10-10 11:32:18','<p><strong>La Vie des Plantes</strong> est une entreprise innovante spécialisée dans la vente de kits de plantes à faire pousser chez soi. Nous proposons une large gamme de plantes, allant des herbes aromatiques aux fleurs exotiques, pour tous les passionnés de jardinage, débutants comme experts. Chaque kit comprend des graines soigneusement sélectionnées, du terreau bio, et des instructions simples pour faciliter la culture à domicile. Notre mission est de reconnecter les gens avec la nature, tout en rendant le jardinage accessible et amusant pour tous.</p>		\n													<img width=\"640\" height=\"427\" src=\"http://la-vie-des-plantes-backup.local/wp-content/uploads/2024/10/volunteers-little-kid-planting-tree-covering-hole-ground-1024x683.jpg\" alt=\"\" srcset=\"http://la-vie-des-plantes-backup.local/wp-content/uploads/2024/10/volunteers-little-kid-planting-tree-covering-hole-ground-1024x683.jpg 1024w, http://la-vie-des-plantes-backup.local/wp-content/uploads/2024/10/volunteers-little-kid-planting-tree-covering-hole-ground-300x200.jpg 300w, http://la-vie-des-plantes-backup.local/wp-content/uploads/2024/10/volunteers-little-kid-planting-tree-covering-hole-ground-768x512.jpg 768w, http://la-vie-des-plantes-backup.local/wp-content/uploads/2024/10/volunteers-little-kid-planting-tree-covering-hole-ground-1536x1024.jpg 1536w, http://la-vie-des-plantes-backup.local/wp-content/uploads/2024/10/volunteers-little-kid-planting-tree-covering-hole-ground-2048x1365.jpg 2048w\" sizes=\"(max-width: 640px) 100vw, 640px\" />','A PROPOS','','publish','closed','closed','','a-propos','','','2024-10-10 16:18:09','2024-10-10 15:18:09','',0,'http://la-vie-des-plantes-backup.local/?page_id=109',0,'page','',0);
