INSERT INTO `wp_wc_admin_notes` VALUES (45,'woocommerce-shipping-fedex-api-outage-2023-07-16','warning','en_US','Scheduled FedEx API outage — July 2023','On July 16 there will be a full outage of the FedEx API from 04:00 to 08:00 AM UTC. Due to planned maintenance by FedEx, you\'ll be unable to provide FedEx shipping rates during this time. Follow the link below for more information and recommendations on how to minimize impact.','{}','pending','woocommerce.com','2024-10-10 15:26:21',NULL,0,'plain','',0,0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (46,'wcship-2023-07-hazmat-update-needed','update','en_US','Action required: USPS HAZMAT compliance update for WooCommerce Shipping & Tax extension','<strong>Your store requires an update for the WooCommerce Shipping extension</strong>. Please update to the latest version of the WooCommerce Shipping &amp; Tax extension immediately to ensure compliance with new USPS HAZMAT rules currently in effect.','{}','pending','woocommerce.com','2024-10-10 15:26:21',NULL,0,'plain','',0,0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (47,'woocommerce-WCStripe-Aug-2023-update-needed','update','en_US','Action required: Security update for WooCommerce Stripe plugin','<strong>Your shop requires an important security update for the  WooCommerce Stripe plugin</strong>. The fix for this vulnerability was released on July 31. Please update immediately.','{}','pending','woocommerce.com','2024-10-10 15:26:21',NULL,0,'plain','',0,0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (48,'woocommerce-WCStripe-Aug-2023-security-updated','update','en_US','Security update of WooCommerce Stripe plugin','<strong>Your store has been updated to the latest secure version of the WooCommerce Stripe plugin</strong>. This update was released on July 31.','{}','pending','woocommerce.com','2024-10-10 15:26:21',NULL,0,'plain','',0,0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (49,'woocommerce-WooPayments-Aug-2023-security-updated','update','en_US','Security update of WooPayments (WooCommerce Payments) plugin','<strong>Your store has been updated to the more secure version of WooPayments (WooCommerce Payments)</strong>. This update was released on July 31.','{}','pending','woocommerce.com','2024-10-10 15:26:21',NULL,0,'plain','',0,0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (50,'avalara_q3-2023_noAvaTax','marketing','en_US','Automatically calculate VAT in real time','Take the effort out of determining tax rates and sell confidently across borders with automated tax management from Avalara AvaTax— including built-in VAT calculation when you sell into or across the EU and UK. Save time and stay compliant when you let Avalara do the heavy lifting.','{}','pending','woocommerce.com','2024-10-10 15:26:21',NULL,0,'plain','',0,0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (51,'woocommerce-usermeta-Sept2023-productvendors','update','en_US','Your store requires a security update','<strong>Your shop needs an update to address a vulnerability in WooCommerce.</strong> The fix was released on Sept 15. Please update WooCommerce to the latest version immediately. <a href=\"https://developer.woocommerce.com/2023/09/16/woocommerce-vulnerability-reintroduced-from-7-0-1/\" />Read our developer update</a> for more information.','{}','pending','woocommerce.com','2024-10-10 15:26:21',NULL,0,'plain','',0,0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (52,'woocommerce-STRIPE-Oct-2023-update-needed','update','en_US','Action required: Security update for WooCommerce Stripe Gateway','<strong>Your shop requires a security update to address a vulnerability in the WooCommerce Stripe Gateway</strong>. The fix for this vulnerability was released on October 17. Please update immediately.','{}','pending','woocommerce.com','2024-10-10 15:26:21',NULL,0,'plain','',0,0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (53,'amazon-mcf-reviews-2023-12-07','marketing','en_US','Enjoying Amazon MCF for WooCommerce?','We\'re Never Settle, the developers behind Amazon MCF for WooCommerce, and would be deeply honored to have your review. Reviews help immensely as other users can learn how MCF can solve their needs too! Not happy or need help? Please reach out for support and we’d love to make things right!','{}','pending','woocommerce.com','2024-10-10 15:26:21',NULL,0,'plain','',0,0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (54,'remove_estimated_deposits_2024','marketing','en_US','Estimated deposits are going away','To provide more accurate deposit information and support the expansion of instant deposits, estimated deposit details will no longer be available in WooPayments. We recommend upgrading to the latest version of WooPayments for more detailed balance status information.','{}','pending','woocommerce.com','2024-10-10 15:26:21',NULL,0,'plain','',0,0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (55,'stripe_securityupdate_q3_2024','update','en_US','Security update required for your Stripe extension','To continue securely accepting payments, you must update your Stripe extension to at least version 8.6, re-authenticate, and turn on the new checkout experience. Review the documentation for instructions on how to make the necessary changes from your WooCommerce dashboard.','{}','pending','woocommerce.com','2024-10-10 15:26:21',NULL,0,'plain','',0,0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (56,'wc-admin-choosing-a-theme','marketing','en_US','Vous cherchez un thème ?','Découvrez les thèmes compatibles avec WooCommerce et choisissez celui qui correspond le mieux à l’image de votre marque et à vos besoins.','{}','unactioned','woocommerce-admin','2024-10-11 15:26:55',NULL,0,'plain','',0,0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (57,'wc-admin-launch-checklist','info','en_US','Prêt à lancer votre boutique ?','Pour ne jamais ressentir cette sensation d’avoir oublié quelque chose, nous avons conçu une liste de contrôle reprenant les points essentiels avant lancement.','{}','unactioned','woocommerce-admin','2024-10-11 15:26:55',NULL,0,'plain','',0,0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (58,'wc-admin-onboarding-payments-reminder','info','en_US','Commencer à accepter les paiements sur votre boutique !','Accepter les paiements avec le fournisseur qui vous convient, parmi plus de 100 passerelles de paiement pour WooCommerce.','{}','unactioned','woocommerce-admin','2024-10-26 13:29:11',NULL,0,'plain','',0,0,'info');
